parameters.jsCode:
const result = $json.parsed;

let offerData = {};

try {
  const webhookData = $('Webhook CV Idéal').first().json.body;
  if (webhookData) {
    offerData = webhookData.offer || {};
  }
} catch (error) {
  console.error('Erreur récupération données webhook:', error);
}

if (!result.idealCV) {
  return [{
    json: {
      success: false,
      error: 'Format de réponse invalide : CV idéal manquant',
      received: result
    }
  }];
}

return [{
  json: {
    success: true,
    idealCV: result.idealCV,
    metadata: {
      offer_title: offerData.title || 'Non spécifié',
      generated_at: new Date().toISOString()
    }
  }
}];
