parameters.jsCode:
const result = $json.parsed;

let offerData = {};
let candidateData = {};

try {
  const webhookData = $('Webhook Lettre Motivation').first().json.body;
  if (webhookData) {
    offerData = webhookData.offer || {};
    candidateData = webhookData.candidate || {};
  }
} catch (error) {
  console.error('Erreur récupération données webhook:', error);
}

if (!result.coverLetter) {
  return [{
    json: {
      success: false,
      error: 'Format de réponse invalide : lettre manquante',
      received: result
    }
  }];
}

return [{
  json: {
    success: true,
    coverLetter: result.coverLetter,
    metadata: {
      offer_title: offerData.title || 'Non spécifié',
      candidate_name: `${candidateData.prenom || ''} ${candidateData.nom || ''}`.trim() || 'Non spécifié',
      generated_at: new Date().toISOString()
    }
  }
}];
