parameters.jsCode:
// Récupère le résultat parsé
const result = $json.parsed;

// Récupère les données originales de l'offre et du candidat
let offerData = {};
let candidateData = {};

try {
  const webhookData = $('Webhook Matcher Offre').first().json.body;
  if (webhookData) {
    offerData = webhookData.offer || {};
    candidateData = webhookData.candidate || {};
  }
} catch (error) {
  console.error('Erreur récupération données webhook:', error);
}

// Valide que les 3 documents sont présents
if (!result.personalizedCV || !result.idealCV || !result.coverLetter) {
  return [{
    json: {
      success: false,
      error: 'Format de réponse invalide : documents manquants',
      received: result
    }
  }];
}

// Structure la réponse finale
return [{
  json: {
    success: true,
    personalizedCV: result.personalizedCV,
    idealCV: result.idealCV,
    coverLetter: result.coverLetter,
    metadata: {
      offer_title: offerData.title || 'Non spécifié',
      candidate_name: `${candidateData.prenom || ''} ${candidateData.nom || ''}`.trim() || 'Non spécifié',
      generated_at: new Date().toISOString()
    }
  }
}];
